mapper.select:
select id,title,content,first_picture,likes, flag,view,description,appreciation,share_statement,commentabled,published, recommend, create_time ,update_time ,type_id from t_blog
and published = true
and title like concat('%',#{title},'%')
and recommend = #{recommend}
and type_id = #{typeId}
order by update_time DESC

select id,title,content,first_picture,likes, flag,view,description,appreciation,share_statement,commentabled,published, recommend, create_time ,update_time ,type_id from t_blog
and title like concat('%',#{title},'%')
and recommend = #{recommend}
and type_id = #{typeId}
order by update_time DESC

mapper.update:
update t_blog set
view = view + 1
where id =#{id}

update t_blog set
likes = likes + #{likes}
where id =#{id}
